import React from 'react';
import axios from 'axios';
import PersonAddRoundedIcon from '@material-ui/icons/PersonAddRounded';
import GitHubIcon from '@material-ui/icons/GitHub';
import ListSubheader from '@material-ui/core/ListSubheader';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
const styleBack = {
    backgroundColor: 'rgba(255, 255, 255, 0.6)',
    height: '700px',
    width: '750px',
    alignItems: 'center',
    borderRadius: "16px",
    margin: '0px auto',
};
const styleTitle = {
    fontSize: "35px",
    fontFamily: "cursive",
}
const styleBlock = {
    marginTop: '30px',
}


const GithubProfile=()=>{
    return (
        <div style = {styleBack}>
            <div style = {styleBlock} >
                <UserGithub></UserGithub>
            </div>
        </div>
    )
}
class UserGithub extends React.Component{
    constructor(props){
        super(props);
        this.JsonUrl = "https://api.github.com/users/yenchunR";
        this.state = {
            userName: '',
            githubUrl: '',
            avataUrl: '',
            Followers: '',
            Followings: '',
            Location: '',
            Blog: '',
        }
    }
    componentDidMount(){
        axios.get(this.JsonUrl).then(result =>{
            console.log(result);
            const data = result.data;
            if(data){
                this.setState({
                    userName: data.login,
                    githubUrl: data.html_url,
                    avataUrl: data.avatar_url,
                    Followers: data.followers,
                    Followings: data.following,
                    Location: data.location,
                    Blog: data.blog,
                });
            }
        });
    }
    render(){
        return(
            <div>
                <img src={this.state.avataUrl} style={{height:250+'px',marginTop:30+'px',borderRadius: 360+'px',}}></img>
                <h3 style={styleTitle}>{this.state.userName}</h3>
                 <ListItem button component="a" href={this.state.githubUrl}>
                    <ListItemIcon><GitHubIcon /></ListItemIcon>
                    <ListItemText primary="Github Link" />
                </ListItem>
                <h3>{this.state.Location}</h3>
                <h3>Followers: {this.state.Followers} Following: {this.state.Followings}</h3>
                <h3>BLOG: {this.state.Blog}</h3>
            </div>
        );
    }
}
export default GithubProfile;
