import React, { Component } from 'react';

import DatePicker from 'react-datepicker/dist/react-datepicker';

class CustomInput extends React.Component {
  render() {
    return (
      <div className="wrapper">
        <i
          onClick={this.props.onClick}
          aria-hidden="true"
          className="fa fa-calendar"
        />
        <input
          onClick={this.props.onClick}
          className="dateInput"
          value={this.props.value}
          type="text"
        />
      </div>
    );
  }
}

class EditForm extends React.Component {
  render() {
    return (
      <div>
        <DatePicker
          selected={this.props.date}
          onChange={this.props.handleChange}
          showTimeSelect
          customInput={<CustomInput />}
          timeFormat="HH:mm"
          value={this.props.startDate}
          dateFormat="yy-MM-dd, hh:mm"
          timeCaption="time"
        />
        <textarea
          onChange={e => this.props.handleDescription(e)}
          value={this.props.description}
        />
        <button onClick={this.props.onSave} type="submit">
          Save
        </button>
        <button onClick={this.props.onCancel} type="submit">
          Cancel
        </button>
      </div>
    );
  }
}

class Todo extends Component {
  state = {
    isEditing: false,
    startDate: new Date('July 27, 1962 23:30:00'),
    description: '',
    second: 120
  };

  setEditing = () => {
    this.setState({
      isEditing: !this.state.isEditing
    });
  };

  handleChange = date => {
    this.setState({
      startDate: date
    });
  };

  handleDescription = evt => {
    this.setState({
      description: evt.target.value
    });
  };

  formatDate = () => {
    const d = this.state.startDate;

    return (
      d
        .getYear()
        .toString()
        .substring(2) +
      '-' +
      ('00' + (d.getMonth() + 1)).slice(-2) +
      '-' +
      ('00' + d.getDate()).slice(-2) 
    );
  };

  onSave = () => {
    const { description } = this.state;

    this.props.update(this.props.index, {
      description,
      date: this.formatDate()
    });

    this.setState({
      isEditing: false
    });
  };

  onCancel = () => {
    this.setState({
      isEditing: false
    });
  };

  componentDidMount = () => {
    const { todo } = this.props;

    this.setState({
      description: todo.description,
      startDate: new Date(todo.date)
    });
  };

  render() {
    const date = new Date(this.state.startDate);
    console.log(date);

    date.toString();
    return (
      <div>
        {this.state.isEditing ? (
          <EditForm
            handleChange={this.handleChange}
            description={this.state.description}
            startDate={date}
            handleDescription={this.handleDescription}
            onSave={this.onSave}
            onCancel={this.onCancel}
            date={this.date}
          />
        ) : (
          <li>
            <div>{this.props.todo.date}</div>
            <div>{this.props.todo.description}</div>
            <button onClick={() => this.setEditing()}>Edit</button>
          </li>
        )}
      </div>
    );
  }
}

export default class Date extends React.Component {
  constructor() {
    super();

    this.state = {
      todos: [
        {
          date: '2019-12-09',
          description: 'Hello'
        },
        {
          date: '2019-11-10',
          description: 'World'
        }
      ]
    };
  }

  update = (index, todo) => {
    this.setState({
      todos: [
        ...this.state.todos.slice(0, index),
        Object.assign({}, this.state.todos[index], todo),
        ...this.state.todos.slice(index + 1)
      ]
    });
  };

  createTodo = todo => {
    const news = this.state.todos.slice();
    news.push(todo);
    this.setState({ todos: news });
  };

  render() {
    return (
      <div>
        <ul>
          {this.state.todos.map((todo, index) => (
            <Todo key={index} index={index} todo={todo} update={this.update} />
          ))}
        </ul>
      </div>
    );
  }
}
