import React from 'react';
import axios from 'axios';

const styleBack = {
    backgroundColor: 'rgba(255, 255, 255, 0.6)',
    height: '400px',
    width: '450px',
    alignItems: 'center',
    borderRadius: "16px",
    margin: '0px auto',
};
const styleTitle = {
    fontSize: "55px",
    fontFamily: "cursive",
}
const styleBlock = {
    marginTop: '165px',
}


const GithubProfile=()=>{
    return (
        <div style = {styleBack}>
            <div style = {styleBlock} >
                <h1 style={styleTitle}>Login</h1>
                <UserGithub></UserGithub>
            </div>
        </div>
    )
}
class UserGithub extends React.Component{
    constructor(props){
        super(props);
        this.JsonUrl = "https://api.github.com/users/cjwu";
        this.state = {
            userName: '',
            githubUrl: '',
            avataUrl: '',
            Email: '',
        }
    }
    componentDidMount(){
        axios.get(this.JsonUrl).then(result =>{
            console.log(result);
            const data = result.data;
            if(data){
                this.setState({
                    userName: data.name,
                    githubUrl: data.html_url,
                    avataUrl: data.avatar_url,
                    Email: data.email,
                });
            }
        });
    }
    render(){
        return(
            <div>
                <h3>{this.state.userName}</h3>
                <img src={this.state.avataUrl}></img>
                <a href={this.state.githubUrl}>Github Link</a>
                <h3>{this.state.Email}</h3>
            </div>
        );
    }
}
export default GithubProfile;
