import Button from '@material-ui/core/Button';
import React, { Component } from 'react';

const changeText=(event)=>{
    console.log(event.target)
    event.target.innerText = event.target.innerText + "被點了了"
}

const MultiButton=(num)=>{
    var output=[];
    for(let i=1;i<num+1;++i){
        output.push(<Button variant="outlined" color="primary"
        onClick={changeText}>第{i}個按鍵</Button>)
    }
    output.push(<CounterButton variant="outlined" color="primary"></CounterButton>)
    return output;
}

class CounterButton extends React.Component{
    constructor(props){
      super(props)
      this.state = {
        counter: 0
      }
      this.handleClick = this.handleClick = this.handleClick.bind(this)
    }
    handleClick(event){
      this.setState({counter: this.state.counter+1});
      console.log(this.state);
    }
    render() {
      var text = this.state.counter;
      return (
       <Button onClick={this.handleClick}>
           你點了 {text} 次.
       </Button>
      );
    }
  }
export default MultiButton;
