import React, { useState } from "react";
import DatePicker from "react-datepicker";
import { getMonth, getYear, getDay } from 'date-fns';
import Button from '@material-ui/core/Button';
import "react-datepicker/dist/react-datepicker.css";

// CSS Modules, react-datepicker-cssmodules.css
// import 'react-datepicker/dist/react-datepicker-cssmodules.css';

export default class DatePick extends React.Component{
    constructor(props){
        super(props);
        this.showYear = this.showYear.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.state = {
            startDate: new Date(),
        }
    }
    showYear= date =>{
        var Year = getYear(date) - 1911;
        var Mon = getMonth(date) + 1;
        alert(date);
    }
    handleChange = date => {
        alert(getMonth(date));
        this.setState({
          startDate: date
        });
      };

    handleDate = date => {
        var Year = getYear(date) - 1911;
        var Mon = getMonth(date) + 1;
        var NewDate = Year.toString() + Mon.toString();
        this.setState({
            startDate: NewDate
          });
    }
    render(){
        var date = this.state.startDate;
        return (
            <div>
                <DatePicker 
                name="dateOne"
                dateFormat="mm"
                selected={this.state.startDate} 
                onChange={this.handleChange}
                />
                <Button onClick={this.showYear} />
            </div>
        );
  }

};
