import React, { useState } from "react";
import DatePicker from "react-datepicker";
import { getMonth, getYear, getDate } from 'date-fns';
import Button from '@material-ui/core/Button';
import "react-datepicker/dist/react-datepicker.css";

// CSS Modules, react-datepicker-cssmodules.css
// import 'react-datepicker/dist/react-datepicker-cssmodules.css';

export default class DatePick extends React.Component{

    constructor(props){
        super(props);
        this.showYear = this.showYear.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.state = {
            startDate: new Date(110, 2, 10),
        }
    }
    showYear= date =>{
        var Year = getYear(date) - 1911;
        var Mon = getMonth(date) + 1;
        alert(date);
    }
    handleChange = date => {

        var Year = getYear(date) - 1911;
        var Mon = getMonth(date) + 1;
        var Day = getDate(date);

        var NewDate = new Date(Date.parse(Year.toString()+'/'+ Mon.toString() +'/'+ Day.toString()));

        alert(NewDate);
        this.setState({
          startDate: NewDate,
        });
      };


  render(){
    return (
        <div>
            <DatePicker 
                dateFormat="民國yyy年 MM月 d日"
                selected={this.state.startDate} 
                onChange={this.handleChange}
            />

        </div>

      );
  }

};
