import React from 'react';
import axios from 'axios';
import PersonAddRoundedIcon from '@material-ui/icons/PersonAddRounded';
const styleBack = {
    backgroundColor: 'rgba(255, 255, 255, 0.6)',
    height: '700px',
    width: '750px',
    alignItems: 'center',
    borderRadius: "16px",
    margin: '0px auto',
};
const styleTitle = {
    fontSize: "45px",
    fontFamily: "cursive",
}
const styleBlock = {
    marginTop: '165px',
}


const GithubProfile=()=>{
    return (
        <div style = {styleBack}>
            <div style = {styleBlock} >
                <h1 style={styleTitle}>Login</h1>
                <UserGithub></UserGithub>
            </div>
        </div>
    )
}
class UserGithub extends React.Component{
    constructor(props){
        super(props);
        this.JsonUrl = "https://api.github.com/users/yenchunR";
        this.state = {
            userName: '',
            githubUrl: '',
            avataUrl: '',
            Followers: '',
            Followings: '',
            Location: '',
            Blog: '',
        }
    }
    componentDidMount(){
        axios.get(this.JsonUrl).then(result =>{
            console.log(result);
            const data = result.data;
            if(data){
                this.setState({
                    userName: data.login,
                    githubUrl: data.html_url,
                    avataUrl: data.avatar_url,
                    Followers: data.followers,
                    Followings: data.following,
                    Location: data.location,
                    Blog: data.blog,
                });
            }
        });
    }
    render(){
        return(
            <div>
                <h3>{this.state.userName}</h3>
                <img src={this.state.avataUrl} style={{height:120+'px',}}></img>
                <br></br>
                <a href={this.state.githubUrl}>Github Link</a>
                <h3>{this.state.Location}</h3>
                <h3>Followers: {this.state.Followers} Following: {this.state.Followings}</h3>
                <h3>BLOG: {this.state.Blog}</h3>
            </div>
        );
    }
}
export default GithubProfile;
